mapper.select:
SELECT DISTINCT




        AND (
        p.name LIKE CONCAT('%', #{search}, '%')
        OR p.description LIKE CONCAT('%', #{search}, '%')
        )


        AND pr.process_id = #{processId}


        AND pl.production_line_id = #{productId}


ORDER BY p.name ASC

SELECT COUNT(DISTINCT p.part_id)



        AND (
        p.name LIKE CONCAT('%', #{search}, '%')
        OR p.description LIKE CONCAT('%', #{search}, '%')
        )


        AND pr.process_id = #{processId}


        AND pl.production_line_id = #{productId}
